const getAppToken = async (loginToken, motion) => {

    // const url = 'get_app_token_url';
    const headers = {
        'User-Agent': 'MiFit/5.3.0 (iPhone; iOS 14.7.1; Scale/3.00)',
        'X-Forwarded-For': motion.fakeIpAddr
    };

    console.log("get_app_token_url", url3 + loginToken)
    console.log("get_app_token_url headers", headers)
    try {
        const response = await axios.get(url3 + loginToken, {headers});
        console.log("app_token获取成功！", response.data);
        console.log("app_token获取成功！", response.data.token_info.app_token);
        return response.data.token_info.app_token;
    } catch (error) {
        console.error('Error fetching app_token:', error);
        throw error;
    }
}


function getAccessToken(location) {
    if (location) {
        const match = location.match(/(?<=access=).*?(?=&)/);
        return match ? match[0] : null;
    }
    return null;
}


